import { forwardRef, useState } from 'react';
import { Eye, EyeOff } from 'lucide-react';
import { cn } from '../../../utils/helpers';
import './Input.css';

/**
 * Reusable Input component with validation and icons
 */
const Input = forwardRef(({
    type = 'text',
    label,
    placeholder,
    value,
    onChange,
    onBlur,
    error,
    helperText,
    leftIcon,
    rightIcon,
    disabled = false,
    required = false,
    fullWidth = true,
    className = '',
    id,
    name,
    ...props
}, ref) => {
    const [showPassword, setShowPassword] = useState(false);
    const inputId = id || name || `input-${Math.random().toString(36).substr(2, 9)}`;

    const isPassword = type === 'password';
    const inputType = isPassword && showPassword ? 'text' : type;

    const inputClasses = cn(
        'input-wrapper',
        { 'input-error': error },
        { 'input-disabled': disabled },
        { 'input-full': fullWidth },
        { 'has-left-icon': leftIcon },
        { 'has-right-icon': rightIcon || isPassword },
        className
    );

    return (
        <div className={inputClasses}>
            {label && (
                <label htmlFor={inputId} className="input-label">
                    {label}
                    {required && <span className="input-required">*</span>}
                </label>
            )}
            <div className="input-container">
                {leftIcon && <span className="input-icon input-icon-left">{leftIcon}</span>}
                <input
                    ref={ref}
                    id={inputId}
                    name={name}
                    type={inputType}
                    placeholder={placeholder}
                    value={value}
                    onChange={onChange}
                    onBlur={onBlur}
                    disabled={disabled}
                    required={required}
                    className="input-field"
                    aria-invalid={error ? 'true' : 'false'}
                    aria-describedby={error ? `${inputId}-error` : undefined}
                    {...props}
                />
                {isPassword && (
                    <button
                        type="button"
                        className="input-icon input-icon-right input-password-toggle"
                        onClick={() => setShowPassword(!showPassword)}
                        tabIndex={-1}
                        aria-label={showPassword ? 'Hide password' : 'Show password'}
                    >
                        {showPassword ? <EyeOff size={18} /> : <Eye size={18} />}
                    </button>
                )}
                {!isPassword && rightIcon && (
                    <span className="input-icon input-icon-right">{rightIcon}</span>
                )}
            </div>
            {(error || helperText) && (
                <span
                    id={`${inputId}-error`}
                    className={cn('input-helper', { 'input-helper-error': error })}
                >
                    {error || helperText}
                </span>
            )}
        </div>
    );
});

Input.displayName = 'Input';

export default Input;

import { motion } from 'framer-motion';
import './Loading.css';

/**
 * Loading spinner component
 */
export const Spinner = ({ size = 'md', color = 'primary', className = '' }) => (
    <div className={`spinner spinner-${size} spinner-${color} ${className}`}>
        <div className="spinner-circle" />
    </div>
);

/**
 * Full page loading overlay
 */
export const PageLoader = ({ message = 'Loading...' }) => (
    <motion.div
        className="page-loader"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        exit={{ opacity: 0 }}
    >
        <div className="page-loader-content">
            <Spinner size="lg" />
            <p className="page-loader-message">{message}</p>
        </div>
    </motion.div>
);

/**
 * Section loading skeleton
 */
export const SectionLoader = ({ height = 400 }) => (
    <div className="section-loader" style={{ height }}>
        <Spinner size="md" />
    </div>
);

/**
 * Card skeleton loader
 */
export const CardSkeleton = ({ count = 1 }) => (
    <div className="skeleton-grid">
        {Array.from({ length: count }).map((_, i) => (
            <div key={i} className="card-skeleton">
                <div className="skeleton skeleton-image" />
                <div className="skeleton-content">
                    <div className="skeleton skeleton-title" />
                    <div className="skeleton skeleton-text" />
                    <div className="skeleton skeleton-text skeleton-text-short" />
                </div>
            </div>
        ))}
    </div>
);

/**
 * Text skeleton loader
 */
export const TextSkeleton = ({ lines = 3, className = '' }) => (
    <div className={`text-skeleton ${className}`}>
        {Array.from({ length: lines }).map((_, i) => (
            <div
                key={i}
                className={`skeleton skeleton-line ${i === lines - 1 ? 'skeleton-line-short' : ''}`}
            />
        ))}
    </div>
);

const Loading = {
    Spinner,
    PageLoader,
    SectionLoader,
    CardSkeleton,
    TextSkeleton,
};

export default Loading;

