import { forwardRef } from 'react';
import { motion } from 'framer-motion';
import { Loader2 } from 'lucide-react';
import { cn } from '../../../utils/helpers';
import './Button.css';

/**
 * Reusable Button component with variants and loading state
 * Supports `as` prop to render as a different element (e.g., Link)
 */
const Button = forwardRef(({
    children,
    variant = 'primary',
    size = 'md',
    isLoading = false,
    disabled = false,
    fullWidth = false,
    leftIcon,
    rightIcon,
    className = '',
    onClick,
    type = 'button',
    as,
    ...props
}, ref) => {
    const buttonClasses = cn(
        'btn',
        `btn-${variant}`,
        `btn-${size}`,
        { 'btn-full': fullWidth },
        { 'btn-loading': isLoading },
        className
    );

    const Component = as ? motion(as) : motion.button;

    const elementProps = {
        ref,
        className: buttonClasses,
        onClick,
        whileHover: { scale: disabled || isLoading ? 1 : 1.02 },
        whileTap: { scale: disabled || isLoading ? 1 : 0.98 },
        ...props,
    };

    // Only add button-specific props when rendering as a button
    if (!as) {
        elementProps.type = type;
        elementProps.disabled = disabled || isLoading;
    }

    return (
        <Component {...elementProps}>
            {isLoading ? (
                <Loader2 className="btn-spinner" />
            ) : (
                <>
                    {leftIcon && <span className="btn-icon btn-icon-left">{leftIcon}</span>}
                    <span className="btn-text">{children}</span>
                    {rightIcon && <span className="btn-icon btn-icon-right">{rightIcon}</span>}
                </>
            )}
        </Component>
    );
});

Button.displayName = 'Button';

export default Button;
